var gulp = require('gulp');
var config = require('../config');
var fs = require('fs');
var json = require('comment-json');
var glob = require('glob').Glob;

gulp.task('check:file', function () {
    new glob('./dist/**/*[\u4e00-\u9fa5]{1,}*', {}, function (err, matches) {
        if (matches.length > 0) {
            console.log('ERROR: 产出包中文件不得以中文或非法字符命名:'.red);
            for (var index in matches) {
                console.log('    ' + matches[index].red);
            }
            process.exit(1);
        }
    });
});

gulp.task('check', ['check:file']);
// gulp.task('check:prod', ['check:file']);
