cordova.define("com.jiedaibao.showclosebutton.ShowCloseButton", function (require, exports, module) {
   var exec = require('cordova/exec');
   module.exports = {
       showCloseButton : function(showCloseButton, successCallback, errorCallback) {
       exec(
            successCallback,
            errorCallback,
            'ShowCloseButton',
            'showCloseButton',
            [{
             "showCloseButton": showCloseButton
             }]
            );
       }
    }
  }
);
