var util = {};
var divideStr = '=========================';

/**
 * 自定义的log输出形式
 * @param  {[type]} message [description]
 * @return {[type]}         [description]
 */
util.log = function (message) {
    console.log(divideStr);
    console.log(message);
    console.log(divideStr);
};

module.exports = util;
